mapper.update:
update area set
        id=#{updateDO.id}
        code=#{updateDO.code}
        name=#{updateDO.name}
        pid=#{updateDO.pid}
        pcode=#{updateDO.pcode}
        longitude=#{updateDO.longitude}
        latitude=#{updateDO.latitude}
        level_path=#{updateDO.levelPath}
        level=#{updateDO.level}
        deep=#{updateDO.deep}
        user_id_create=#{updateDO.userIdCreate}
        user_id_modified=#{updateDO.userIdModified}
        gmt_modified=CURRENT_TIMESTAMP
 where is_deleted=0

   and  ${propertyName}=#{values.[${index}]}

